declare module "react-native-qualtrics" {

    export class InitializationResult {
        passed: boolean
        message: string

        constructor(passed: boolean, message: string)
    }

    export class TargetingResult {
        passed: boolean
        surveyUrl: string

        constructor(passed: boolean, surveyUrl: string)

        recordClick(): void
        recordImpression(): void
    }

    export function initialize(brandId: string, projectId: string, interceptId: string, callback?: (result: InitializationResult) => void): void
    export function evaluateTargetingLogic(callback: (result: TargetingResult) => void): void
    export function display(): void
    export function registerViewVisit(viewName: string): void
    export function resetTimer(): void
    export function resetViewCounter(): void
    export function setString(propertyKey: string, propertyString: string): void
    export function setNumber(propertyKey: string, propertyNumber: number): void
    export function setDateTime(propertyKey: string): void
    export function setNotificationIconAsset(propertyAsset: string): void
    export function displayTarget(): void
}
